mapper.select:
    SELECT b.book_id,b.book_name,b.book_info_id,b.category,b.imgurl FROM 
(SELECT * FROM book b,book_info bi WHERE b.book_id=bi.book_info_id AND b.state=1 )b

 	book_name LIKE concat(concat('%',#{bookName}),'%') 
 	AND category=#{bookInfo.category}  
 	AND author LIKE concat(concat('%',#{bookInfo.author}),'%')  
 	AND pub_date > #{bookInfo.pubDate}
